import React, { useState, useEffect } from 'react';
import {
  Container,
  Typography,
  Box,
  Tabs,
  Tab,
  Paper,
  Button,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  IconButton,
  Chip,
  Switch,
  FormControlLabel,
  Alert,
  Snackbar
} from '@mui/material';
import {
  Add as AddIcon,
  Edit as EditIcon,
  Delete as DeleteIcon,
  Visibility as ViewIcon,
  DragIndicator as DragIcon
} from '@mui/icons-material';
import { categoryService, faqService } from '../services/api';

function TabPanel({ children, value, index, ...other }) {
  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`admin-tabpanel-${index}`}
      aria-labelledby={`admin-tab-${index}`}
      {...other}
    >
      {value === index && (
        <Box sx={{ p: 3 }}>
          {children}
        </Box>
      )}
    </div>
  );
}

function AdminPanel() {
  const [tabValue, setTabValue] = useState(0);
  const [categories, setCategories] = useState([]);
  const [faqs, setFaqs] = useState([]);
  const [openDialog, setOpenDialog] = useState(false);
  const [dialogType, setDialogType] = useState(''); // 'category' or 'faq'
  const [dialogMode, setDialogMode] = useState(''); // 'create' or 'edit'
  const [selectedItem, setSelectedItem] = useState(null);
  const [snackbar, setSnackbar] = useState({ open: false, message: '', severity: 'success' });

  // Form states
  const [categoryForm, setCategoryForm] = useState({
    name: '',
    description: '',
    displayOrder: 0,
    active: true
  });

  const [faqForm, setFaqForm] = useState({
    question: '',
    answer: '',
    categoryId: '',
    tags: '',
    priority: 0,
    active: true,
    metaDescription: ''
  });

  useEffect(() => {
    loadCategories();
    loadFaqs();
  }, []);

  const loadCategories = async () => {
    try {
      const response = await categoryService.getAll();
      setCategories(response.data);
    } catch (error) {
      showSnackbar('Erro ao carregar categorias', 'error');
    }
  };

  const loadFaqs = async () => {
    try {
      const response = await faqService.getAll();
      setFaqs(response.data);
    } catch (error) {
      showSnackbar('Erro ao carregar FAQs', 'error');
    }
  };

  const showSnackbar = (message, severity = 'success') => {
    setSnackbar({ open: true, message, severity });
  };

  const handleTabChange = (event, newValue) => {
    setTabValue(newValue);
  };

  const openCreateDialog = (type) => {
    setDialogType(type);
    setDialogMode('create');
    setSelectedItem(null);
    if (type === 'category') {
      setCategoryForm({ name: '', description: '', displayOrder: 0, active: true });
    } else {
      setFaqForm({ question: '', answer: '', categoryId: '', tags: '', priority: 0, active: true, metaDescription: '' });
    }
    setOpenDialog(true);
  };

  const openEditDialog = (type, item) => {
    setDialogType(type);
    setDialogMode('edit');
    setSelectedItem(item);
    if (type === 'category') {
      setCategoryForm({
        name: item.name,
        description: item.description || '',
        displayOrder: item.displayOrder || 0,
        active: item.active
      });
    } else {
      setFaqForm({
        question: item.question,
        answer: item.answer,
        categoryId: item.categoryId,
        tags: item.tags || '',
        priority: item.priority || 0,
        active: item.active,
        metaDescription: item.metaDescription || ''
      });
    }
    setOpenDialog(true);
  };

  const handleSave = async () => {
    try {
      if (dialogType === 'category') {
        if (dialogMode === 'create') {
          await categoryService.create(categoryForm);
          showSnackbar('Categoria criada com sucesso!');
        } else {
          await categoryService.update(selectedItem.id, categoryForm);
          showSnackbar('Categoria atualizada com sucesso!');
        }
        loadCategories();
      } else {
        const faqData = {
          ...faqForm,
          tags: faqForm.tags.split(',').map(tag => tag.trim()).filter(tag => tag)
        };
        if (dialogMode === 'create') {
          await faqService.create(faqData);
          showSnackbar('FAQ criada com sucesso!');
        } else {
          await faqService.update(selectedItem.id, faqData);
          showSnackbar('FAQ atualizada com sucesso!');
        }
        loadFaqs();
      }
      setOpenDialog(false);
    } catch (error) {
      showSnackbar('Erro ao salvar', 'error');
    }
  };

  const handleDelete = async (type, id) => {
    if (window.confirm('Tem certeza que deseja excluir este item?')) {
      try {
        if (type === 'category') {
          await categoryService.delete(id);
          showSnackbar('Categoria excluída com sucesso!');
          loadCategories();
        } else {
          await faqService.delete(id);
          showSnackbar('FAQ excluída com sucesso!');
          loadFaqs();
        }
      } catch (error) {
        showSnackbar('Erro ao excluir', 'error');
      }
    }
  };

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      <Typography variant="h4" component="h1" gutterBottom>
        Painel de Administração - FAQ
      </Typography>

      <Paper sx={{ width: '100%', mb: 2 }}>
        <Tabs value={tabValue} onChange={handleTabChange} aria-label="admin tabs">
          <Tab label="Categorias" />
          <Tab label="FAQs" />
        </Tabs>

        <TabPanel value={tabValue} index={0}>
          <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 2 }}>
            <Typography variant="h6">Gerenciar Categorias</Typography>
            <Button
              variant="contained"
              startIcon={<AddIcon />}
              onClick={() => openCreateDialog('category')}
            >
              Nova Categoria
            </Button>
          </Box>

          <TableContainer component={Paper}>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>Nome</TableCell>
                  <TableCell>Descrição</TableCell>
                  <TableCell>Ordem</TableCell>
                  <TableCell>Status</TableCell>
                  <TableCell>Ações</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {categories.map((category) => (
                  <TableRow key={category.id}>
                    <TableCell>{category.name}</TableCell>
                    <TableCell>{category.description}</TableCell>
                    <TableCell>{category.displayOrder}</TableCell>
                    <TableCell>
                      <Chip 
                        label={category.active ? 'Ativo' : 'Inativo'} 
                        color={category.active ? 'success' : 'default'}
                        size="small"
                      />
                    </TableCell>
                    <TableCell>
                      <IconButton onClick={() => openEditDialog('category', category)}>
                        <EditIcon />
                      </IconButton>
                      <IconButton onClick={() => handleDelete('category', category.id)}>
                        <DeleteIcon />
                      </IconButton>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
        </TabPanel>

        <TabPanel value={tabValue} index={1}>
          <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 2 }}>
            <Typography variant="h6">Gerenciar FAQs</Typography>
            <Button
              variant="contained"
              startIcon={<AddIcon />}
              onClick={() => openCreateDialog('faq')}
            >
              Nova FAQ
            </Button>
          </Box>

          <TableContainer component={Paper}>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>Pergunta</TableCell>
                  <TableCell>Categoria</TableCell>
                  <TableCell>Prioridade</TableCell>
                  <TableCell>Status</TableCell>
                  <TableCell>Visualizações</TableCell>
                  <TableCell>Ações</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {faqs.map((faq) => (
                  <TableRow key={faq.id}>
                    <TableCell sx={{ maxWidth: 300 }}>
                      <Typography variant="body2" noWrap>
                        {faq.question}
                      </Typography>
                    </TableCell>
                    <TableCell>{faq.categoryName}</TableCell>
                    <TableCell>{faq.priority}</TableCell>
                    <TableCell>
                      <Chip 
                        label={faq.active ? 'Ativo' : 'Inativo'} 
                        color={faq.active ? 'success' : 'default'}
                        size="small"
                      />
                    </TableCell>
                    <TableCell>{faq.viewCount || 0}</TableCell>
                    <TableCell>
                      <IconButton onClick={() => openEditDialog('faq', faq)}>
                        <EditIcon />
                      </IconButton>
                      <IconButton onClick={() => handleDelete('faq', faq.id)}>
                        <DeleteIcon />
                      </IconButton>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
        </TabPanel>
      </Paper>

      {/* Dialog for Create/Edit */}
      <Dialog open={openDialog} onClose={() => setOpenDialog(false)} maxWidth="md" fullWidth>
        <DialogTitle>
          {dialogMode === 'create' ? 'Criar' : 'Editar'} {dialogType === 'category' ? 'Categoria' : 'FAQ'}
        </DialogTitle>
        <DialogContent>
          {dialogType === 'category' ? (
            <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2, mt: 1 }}>
              <TextField
                label="Nome"
                value={categoryForm.name}
                onChange={(e) => setCategoryForm({ ...categoryForm, name: e.target.value })}
                required
                fullWidth
              />
              <TextField
                label="Descrição"
                value={categoryForm.description}
                onChange={(e) => setCategoryForm({ ...categoryForm, description: e.target.value })}
                multiline
                rows={3}
                fullWidth
              />
              <TextField
                label="Ordem de Exibição"
                type="number"
                value={categoryForm.displayOrder}
                onChange={(e) => setCategoryForm({ ...categoryForm, displayOrder: parseInt(e.target.value) })}
                fullWidth
              />
              <FormControlLabel
                control={
                  <Switch
                    checked={categoryForm.active}
                    onChange={(e) => setCategoryForm({ ...categoryForm, active: e.target.checked })}
                  />
                }
                label="Ativo"
              />
            </Box>
          ) : (
            <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2, mt: 1 }}>
              <TextField
                label="Pergunta"
                value={faqForm.question}
                onChange={(e) => setFaqForm({ ...faqForm, question: e.target.value })}
                required
                fullWidth
                multiline
                rows={2}
              />
              <TextField
                label="Resposta"
                value={faqForm.answer}
                onChange={(e) => setFaqForm({ ...faqForm, answer: e.target.value })}
                required
                fullWidth
                multiline
                rows={4}
              />
              <FormControl fullWidth required>
                <InputLabel>Categoria</InputLabel>
                <Select
                  value={faqForm.categoryId}
                  onChange={(e) => setFaqForm({ ...faqForm, categoryId: e.target.value })}
                >
                  {categories.filter(cat => cat.active).map((category) => (
                    <MenuItem key={category.id} value={category.id}>
                      {category.name}
                    </MenuItem>
                  ))}
                </Select>
              </FormControl>
              <TextField
                label="Tags (separadas por vírgula)"
                value={faqForm.tags}
                onChange={(e) => setFaqForm({ ...faqForm, tags: e.target.value })}
                fullWidth
                helperText="Ex: suporte, técnico, instalação"
              />
              <TextField
                label="Prioridade"
                type="number"
                value={faqForm.priority}
                onChange={(e) => setFaqForm({ ...faqForm, priority: parseInt(e.target.value) })}
                fullWidth
                helperText="Maior número = maior prioridade"
              />
              <TextField
                label="Meta Descrição"
                value={faqForm.metaDescription}
                onChange={(e) => setFaqForm({ ...faqForm, metaDescription: e.target.value })}
                fullWidth
                helperText="Descrição para SEO (opcional)"
              />
              <FormControlLabel
                control={
                  <Switch
                    checked={faqForm.active}
                    onChange={(e) => setFaqForm({ ...faqForm, active: e.target.checked })}
                  />
                }
                label="Ativo"
              />
            </Box>
          )}
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenDialog(false)}>Cancelar</Button>
          <Button onClick={handleSave} variant="contained">
            {dialogMode === 'create' ? 'Criar' : 'Salvar'}
          </Button>
        </DialogActions>
      </Dialog>

      {/* Snackbar for notifications */}
      <Snackbar
        open={snackbar.open}
        autoHideDuration={6000}
        onClose={() => setSnackbar({ ...snackbar, open: false })}
      >
        <Alert severity={snackbar.severity} onClose={() => setSnackbar({ ...snackbar, open: false })}>
          {snackbar.message}
        </Alert>
      </Snackbar>
    </Container>
  );
}

export default AdminPanel;
