import React, { useState, useEffect } from 'react';
import {
  Container,
  Typography,
  Box,
  TextField,
  InputAdornment,
  Accordion,
  AccordionSummary,
  AccordionDetails,
  Card,
  CardContent,
  Chip,
  IconButton,
  Divider,
  Paper,
  Grid,
  Button,
  Collapse,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  Badge,
  CircularProgress,
  Alert
} from '@mui/material';
import {
  Search as SearchIcon,
  ExpandMore as ExpandMoreIcon,
  Folder as FolderIcon,
  FolderOpen as FolderOpenIcon,
  QuestionAnswer as QuestionIcon,
  ThumbUp as ThumbUpIcon,
  ThumbDown as ThumbDownIcon,
  Visibility as ViewIcon,
  Clear as ClearIcon
} from '@mui/icons-material';
import { categoryService, faqService } from '../services/api';

function PublicFAQ() {
  const [categories, setCategories] = useState([]);
  const [faqs, setFaqs] = useState([]);
  const [filteredFaqs, setFilteredFaqs] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [expandedCategories, setExpandedCategories] = useState(new Set());
  const [expandedFaqs, setExpandedFaqs] = useState(new Set());
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [mostViewed, setMostViewed] = useState([]);
  const [mostHelpful, setMostHelpful] = useState([]);

  useEffect(() => {
    loadData();
  }, []);

  useEffect(() => {
    filterFaqs();
  }, [searchTerm, selectedCategory, faqs]);

  const loadData = async () => {
    try {
      setLoading(true);
      const [categoriesRes, faqsRes, mostViewedRes, mostHelpfulRes] = await Promise.all([
        categoryService.getActiveWithCount(),
        faqService.getActive(),
        faqService.getMostViewed(5),
        faqService.getMostHelpful(5)
      ]);

      setCategories(categoriesRes.data);
      setFaqs(faqsRes.data);
      setMostViewed(mostViewedRes.data);
      setMostHelpful(mostHelpfulRes.data);
    } catch (error) {
      setError('Erro ao carregar dados. Tente novamente mais tarde.');
      console.error('Error loading data:', error);
    } finally {
      setLoading(false);
    }
  };

  const filterFaqs = () => {
    let filtered = faqs;

    if (selectedCategory) {
      filtered = filtered.filter(faq => faq.categoryId === selectedCategory.id);
    }

    if (searchTerm.trim()) {
      const term = searchTerm.toLowerCase();
      filtered = filtered.filter(faq => 
        faq.question.toLowerCase().includes(term) ||
        faq.answer.toLowerCase().includes(term) ||
        (faq.tags && faq.tags.some(tag => tag.toLowerCase().includes(term)))
      );
    }

    // Sort by priority and then by display order
    filtered.sort((a, b) => {
      if (a.priority !== b.priority) {
        return (b.priority || 0) - (a.priority || 0);
      }
      return (a.displayOrder || 0) - (b.displayOrder || 0);
    });

    setFilteredFaqs(filtered);
  };

  const handleCategoryClick = (category) => {
    if (selectedCategory?.id === category.id) {
      setSelectedCategory(null);
      setExpandedCategories(prev => {
        const newSet = new Set(prev);
        newSet.delete(category.id);
        return newSet;
      });
    } else {
      setSelectedCategory(category);
      setExpandedCategories(prev => new Set(prev).add(category.id));
    }
  };

  const handleFaqClick = async (faq) => {
    if (expandedFaqs.has(faq.id)) {
      setExpandedFaqs(prev => {
        const newSet = new Set(prev);
        newSet.delete(faq.id);
        return newSet;
      });
    } else {
      setExpandedFaqs(prev => new Set(prev).add(faq.id));
      // Increment view count
      try {
        await faqService.incrementView(faq.id);
      } catch (error) {
        console.error('Error incrementing view count:', error);
      }
    }
  };

  const handleHelpful = async (faqId, isHelpful) => {
    try {
      if (isHelpful) {
        await faqService.markHelpful(faqId);
      } else {
        await faqService.markNotHelpful(faqId);
      }
    } catch (error) {
      console.error('Error marking helpful:', error);
    }
  };

  const clearSearch = () => {
    setSearchTerm('');
    setSelectedCategory(null);
    setExpandedCategories(new Set());
  };

  const getCategoryFaqCount = (categoryId) => {
    return faqs.filter(faq => faq.categoryId === categoryId).length;
  };

  if (loading) {
    return (
      <Container maxWidth="lg" sx={{ mt: 4, display: 'flex', justifyContent: 'center' }}>
        <CircularProgress />
      </Container>
    );
  }

  if (error) {
    return (
      <Container maxWidth="lg" sx={{ mt: 4 }}>
        <Alert severity="error">{error}</Alert>
      </Container>
    );
  }

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      {/* Header */}
      <Box sx={{ textAlign: 'center', mb: 4 }}>
        <Typography variant="h3" component="h1" gutterBottom color="primary">
          Central de Ajuda - FAQ
        </Typography>
        <Typography variant="h6" color="text.secondary" sx={{ mb: 3 }}>
          Encontre respostas para as perguntas mais frequentes
        </Typography>

        {/* Search Bar */}
        <Paper sx={{ p: 2, maxWidth: 600, mx: 'auto' }}>
          <TextField
            fullWidth
            placeholder="Pesquisar nas perguntas frequentes..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            InputProps={{
              startAdornment: (
                <InputAdornment position="start">
                  <SearchIcon color="action" />
                </InputAdornment>
              ),
              endAdornment: searchTerm && (
                <InputAdornment position="end">
                  <IconButton onClick={clearSearch} size="small">
                    <ClearIcon />
                  </IconButton>
                </InputAdornment>
              )
            }}
          />
          {selectedCategory && (
            <Box sx={{ mt: 1, display: 'flex', alignItems: 'center', gap: 1 }}>
              <Typography variant="body2" color="text.secondary">
                Filtrando por:
              </Typography>
              <Chip
                label={selectedCategory.name}
                onDelete={() => setSelectedCategory(null)}
                color="primary"
                size="small"
              />
            </Box>
          )}
        </Paper>
      </Box>

      <Grid container spacing={3}>
        {/* Sidebar with Categories */}
        <Grid item xs={12} md={3}>
          <Paper sx={{ p: 2 }}>
            <Typography variant="h6" gutterBottom>
              Categorias
            </Typography>
            <List dense>
              {categories.map((category) => {
                const isExpanded = expandedCategories.has(category.id);
                const isSelected = selectedCategory?.id === category.id;
                const faqCount = getCategoryFaqCount(category.id);

                return (
                  <React.Fragment key={category.id}>
                    <ListItem
                      button
                      onClick={() => handleCategoryClick(category)}
                      selected={isSelected}
                      sx={{
                        borderRadius: 1,
                        mb: 0.5,
                        '&.Mui-selected': {
                          backgroundColor: 'primary.light',
                          color: 'primary.contrastText'
                        }
                      }}
                    >
                      <ListItemIcon>
                        {isExpanded ? <FolderOpenIcon /> : <FolderIcon />}
                      </ListItemIcon>
                      <ListItemText 
                        primary={category.name}
                        secondary={category.description}
                      />
                      <Badge badgeContent={faqCount} color="primary" />
                    </ListItem>
                  </React.Fragment>
                );
              })}
            </List>

            {/* Quick Links */}
            <Divider sx={{ my: 2 }} />
            <Typography variant="subtitle2" gutterBottom>
              Acesso Rápido
            </Typography>
            <Box sx={{ display: 'flex', flexDirection: 'column', gap: 1 }}>
              <Button
                size="small"
                startIcon={<ViewIcon />}
                onClick={() => {
                  setSelectedCategory(null);
                  setSearchTerm('');
                }}
              >
                Todas as FAQs
              </Button>
            </Box>
          </Paper>

          {/* Most Viewed */}
          {mostViewed.length > 0 && (
            <Paper sx={{ p: 2, mt: 2 }}>
              <Typography variant="h6" gutterBottom>
                Mais Visualizadas
              </Typography>
              <List dense>
                {mostViewed.map((faq) => (
                  <ListItem key={faq.id} button onClick={() => handleFaqClick(faq)}>
                    <ListItemIcon>
                      <QuestionIcon fontSize="small" />
                    </ListItemIcon>
                    <ListItemText 
                      primary={faq.question}
                      primaryTypographyProps={{ variant: 'body2' }}
                    />
                    <Badge badgeContent={faq.viewCount} color="secondary" />
                  </ListItem>
                ))}
              </List>
            </Paper>
          )}
        </Grid>

        {/* Main Content */}
        <Grid item xs={12} md={9}>
          {filteredFaqs.length === 0 ? (
            <Paper sx={{ p: 4, textAlign: 'center' }}>
              <QuestionIcon sx={{ fontSize: 64, color: 'text.secondary', mb: 2 }} />
              <Typography variant="h6" color="text.secondary" gutterBottom>
                {searchTerm || selectedCategory ? 'Nenhuma FAQ encontrada' : 'Nenhuma FAQ disponível'}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                {searchTerm || selectedCategory 
                  ? 'Tente ajustar sua pesquisa ou selecionar uma categoria diferente.'
                  : 'As FAQs serão exibidas aqui quando estiverem disponíveis.'}
              </Typography>
              {(searchTerm || selectedCategory) && (
                <Button onClick={clearSearch} sx={{ mt: 2 }}>
                  Limpar Filtros
                </Button>
              )}
            </Paper>
          ) : (
            <Box>
              <Typography variant="h5" gutterBottom>
                {selectedCategory ? `${selectedCategory.name}` : 'Todas as Perguntas'}
                <Typography component="span" variant="body2" color="text.secondary" sx={{ ml: 1 }}>
                  ({filteredFaqs.length} {filteredFaqs.length === 1 ? 'pergunta' : 'perguntas'})
                </Typography>
              </Typography>

              {filteredFaqs.map((faq) => {
                const isExpanded = expandedFaqs.has(faq.id);

                return (
                  <Accordion 
                    key={faq.id} 
                    expanded={isExpanded}
                    onChange={() => handleFaqClick(faq)}
                    sx={{ mb: 1 }}
                  >
                    <AccordionSummary expandIcon={<ExpandMoreIcon />}>
                      <Box sx={{ display: 'flex', alignItems: 'center', width: '100%' }}>
                        <QuestionIcon sx={{ mr: 1, color: 'primary.main' }} />
                        <Typography variant="h6" sx={{ flexGrow: 1 }}>
                          {faq.question}
                        </Typography>
                        {faq.priority > 0 && (
                          <Chip label="Importante" color="warning" size="small" sx={{ mr: 1 }} />
                        )}
                        <Typography variant="caption" color="text.secondary">
                          {faq.viewCount || 0} visualizações
                        </Typography>
                      </Box>
                    </AccordionSummary>
                    <AccordionDetails>
                      <Box>
                        <Typography variant="body1" paragraph>
                          {faq.answer}
                        </Typography>

                        {faq.tags && faq.tags.length > 0 && (
                          <Box sx={{ mb: 2 }}>
                            <Typography variant="caption" color="text.secondary" sx={{ mr: 1 }}>
                              Tags:
                            </Typography>
                            {faq.tags.map((tag, index) => (
                              <Chip
                                key={index}
                                label={tag}
                                size="small"
                                variant="outlined"
                                sx={{ mr: 0.5, mb: 0.5 }}
                              />
                            ))}
                          </Box>
                        )}

                        <Divider sx={{ my: 2 }} />

                        <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
                          <Typography variant="body2" color="text.secondary">
                            Esta resposta foi útil?
                          </Typography>
                          <Box>
                            <IconButton 
                              onClick={() => handleHelpful(faq.id, true)}
                              color="success"
                              size="small"
                            >
                              <ThumbUpIcon />
                            </IconButton>
                            <Typography variant="caption" sx={{ mx: 1 }}>
                              {faq.helpfulCount || 0}
                            </Typography>
                            <IconButton 
                              onClick={() => handleHelpful(faq.id, false)}
                              color="error"
                              size="small"
                            >
                              <ThumbDownIcon />
                            </IconButton>
                            <Typography variant="caption" sx={{ ml: 1 }}>
                              {faq.notHelpfulCount || 0}
                            </Typography>
                          </Box>
                        </Box>
                      </Box>
                    </AccordionDetails>
                  </Accordion>
                );
              })}
            </Box>
          )}
        </Grid>
      </Grid>
    </Container>
  );
}

export default PublicFAQ;
