import axios from 'axios';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8080/api';

const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Interceptor para tratamento de erros
api.interceptors.response.use(
  (response) => response,
  (error) => {
    console.error('API Error:', error);
    return Promise.reject(error);
  }
);

// Serviços para Categorias
export const categoryService = {
  getAll: () => api.get('/categories'),
  getActive: () => api.get('/categories/active'),
  getActiveWithCount: () => api.get('/categories/active/with-count'),
  getById: (id) => api.get(`/categories/${id}`),
  create: (category) => api.post('/categories', category),
  update: (id, category) => api.put(`/categories/${id}`, category),
  delete: (id) => api.delete(`/categories/${id}`),
  deactivate: (id) => api.patch(`/categories/${id}/deactivate`),
  reorder: (categoryIds) => api.put('/categories/reorder', categoryIds),
};

// Serviços para FAQs
export const faqService = {
  getAll: () => api.get('/faqs'),
  getActive: () => api.get('/faqs/active'),
  getPaginated: (page = 0, size = 10) => api.get(`/faqs/paginated?page=${page}&size=${size}`),
  getByCategory: (categoryId) => api.get(`/faqs/category/${categoryId}`),
  getByCategoryPaginated: (categoryId, page = 0, size = 10) => 
    api.get(`/faqs/category/${categoryId}/paginated?page=${page}&size=${size}`),
  getById: (id) => api.get(`/faqs/${id}`),
  search: (query) => api.get(`/faqs/search?q=${encodeURIComponent(query)}`),
  searchByCategory: (categoryId, query) => 
    api.get(`/faqs/search/category/${categoryId}?q=${encodeURIComponent(query)}`),
  getByTag: (tag) => api.get(`/faqs/tag/${tag}`),
  getByPriority: (priority) => api.get(`/faqs/priority/${priority}`),
  getMostViewed: (limit = 10) => api.get(`/faqs/most-viewed?limit=${limit}`),
  getMostHelpful: (limit = 10) => api.get(`/faqs/most-helpful?limit=${limit}`),
  create: (faq) => api.post('/faqs', faq),
  update: (id, faq) => api.put(`/faqs/${id}`, faq),
  delete: (id) => api.delete(`/faqs/${id}`),
  deactivate: (id) => api.patch(`/faqs/${id}/deactivate`),
  incrementView: (id) => api.patch(`/faqs/${id}/view`),
  markHelpful: (id) => api.patch(`/faqs/${id}/helpful`),
  markNotHelpful: (id) => api.patch(`/faqs/${id}/not-helpful`),
  reorder: (faqIds) => api.put('/faqs/reorder', faqIds),
};

export default api;
