import { useState, useMemo } from 'react';
import { Search, HelpCircle, FileText, ExternalLink } from 'lucide-react';
import { Input } from '../components/ui/input';
import { Button } from '../components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '../components/ui/card';
import { CategoryCard } from '../components/FAQ/CategoryCard';
import { mockFaqs, mockCategories } from '../services/V1.Mockup.FAQ';
import { Link } from 'react-router-dom';

const FAQ = () => {
  const [searchTerm, setSearchTerm] = useState('');

  // Adaptar dados do V1.Mockup.FAQ para o formato esperado
  const faqsAtivos = mockFaqs.filter(faq => faq.active).map(faq => ({
    id: faq.id.toString(),
    categoria: faq.categoryName,
    pergunta: faq.question,
    resposta: faq.answer,
    tags: faq.tags,
    status: 'ativo',
    prioridade: faq.priority === 'HIGH' ? 1 : faq.priority === 'MEDIUM' ? 2 : 3,
    autor: 'Admin',
    dataCreated: new Date(faq.createdAt),
    dataUpdated: new Date(faq.updatedAt),
    visualizacoes: faq.viewCount
  }));

  const categoriasAtivas = mockCategories.filter(cat => cat.active)
    .sort((a, b) => a.displayOrder - b.displayOrder)
    .map(cat => ({
      id: cat.id.toString(),
      nome: cat.name,
      descricao: cat.description,
      icone: cat.name === 'Conta' ? 'User' : cat.name === 'Pagamento' ? 'CreditCard' : cat.name === 'TÃ©cnico' ? 'Settings' : 'HelpCircle',
      ordem: cat.displayOrder,
      status: 'ativo'
    }));

  const filteredResults = useMemo(() => {
    if (!searchTerm) return { categorias: categoriasAtivas, total: faqsAtivos.length };

    const matchingFAQs = faqsAtivos.filter(faq =>
      faq.pergunta.toLowerCase().includes(searchTerm.toLowerCase()) ||
      faq.resposta.toLowerCase().includes(searchTerm.toLowerCase()) ||
      faq.tags.some(tag => tag.toLowerCase().includes(searchTerm.toLowerCase()))
    );

    const categoriasComResultados = categoriasAtivas.filter(categoria =>
      matchingFAQs.some(faq => faq.categoria === categoria.nome)
    );

    return { categorias: categoriasComResultados, total: matchingFAQs.length };
  }, [searchTerm, faqsAtivos, categoriasAtivas]);

  return (
    <div className="min-h-screen bg-gradient-to-br from-background via-background to-accent/30">
      {/* Header */}
      <div className="border-b border-border/50 bg-card/50 backdrop-blur-sm">
        <div className="container mx-auto px-4 py-8">
          <div className="flex items-center justify-between mb-6">
            <div className="flex items-center gap-3">
              <div className="h-12 w-12 rounded-lg bg-primary/10 flex items-center justify-center">
                <HelpCircle className="h-6 w-6 text-primary" />
              </div>
              <div>
                <h1 className="text-3xl font-bold text-foreground">
                  Central de Ajuda
                </h1>
                <p className="text-muted-foreground">
                  Encontre respostas para suas dÃºvidas mais frequentes
                </p>
              </div>
            </div>
            <Link to="/faq/admin">
              <Button variant="outline" className="gap-2">
                <FileText className="h-4 w-4" />
                Modo Editor
                <ExternalLink className="h-4 w-4" />
              </Button>
            </Link>
          </div>

          {/* Search */}
          <div className="relative max-w-2xl">
            <Search className="absolute left-4 top-1/2 transform -translate-y-1/2 h-5 w-5 text-muted-foreground" />
            <Input
              placeholder="Buscar por perguntas, respostas ou tags..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="pl-12 h-12 text-base border-2 focus:border-primary/50"
            />
          </div>

          {/* Search Results Info */}
          {searchTerm && (
            <div className="mt-4 text-sm text-muted-foreground">
              {filteredResults.total > 0 ? (
                <>Encontrados <strong>{filteredResults.total}</strong> resultados para "{searchTerm}"</>
              ) : (
                <>Nenhum resultado encontrado para "{searchTerm}"</>
              )}
            </div>
          )}
        </div>
      </div>

      {/* Main Content */}
      <div className="container mx-auto px-4 py-8">
        {filteredResults.total === 0 && searchTerm ? (
          <Card className="text-center py-12">
            <CardContent>
              <div className="h-16 w-16 mx-auto mb-4 rounded-full bg-muted flex items-center justify-center">
                <Search className="h-8 w-8 text-muted-foreground" />
              </div>
              <h3 className="text-lg font-semibold mb-2">Nenhum resultado encontrado</h3>
              <p className="text-muted-foreground mb-4">
                Tente usar outros termos de busca ou navegue pelas categorias abaixo.
              </p>
              <Button onClick={() => setSearchTerm('')} variant="outline">
                Limpar busca
              </Button>
            </CardContent>
          </Card>
        ) : (
          <>
            {/* Stats */}
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
              <Card>
                <CardHeader className="pb-3">
                  <CardTitle className="text-base font-medium text-muted-foreground">
                    Total de Perguntas
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="text-2xl font-bold text-primary">{faqsAtivos.length}</div>
                </CardContent>
              </Card>

              <Card>
                <CardHeader className="pb-3">
                  <CardTitle className="text-base font-medium text-muted-foreground">
                    Categorias Ativas
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="text-2xl font-bold text-primary">{categoriasAtivas.length}</div>
                </CardContent>
              </Card>

              <Card>
                <CardHeader className="pb-3">
                  <CardTitle className="text-base font-medium text-muted-foreground">
                    Ãltima AtualizaÃ§Ã£o
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="text-2xl font-bold text-primary">
                    {Math.max(...faqsAtivos.map(f => f.dataUpdated.getTime())) 
                      ? new Date(Math.max(...faqsAtivos.map(f => f.dataUpdated.getTime()))).toLocaleDateString('pt-BR')
                      : 'N/A'
                    }
                  </div>
                </CardContent>
              </Card>
            </div>

            {/* Categories */}
            <div className="space-y-4">
              {filteredResults.categorias.map(categoria => (
                <CategoryCard
                  key={categoria.id}
                  categoria={categoria}
                  faqs={faqsAtivos}
                  searchTerm={searchTerm}
                />
              ))}
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default FAQ;
